Shape=function(a,b,c,d){this.init(a,b,c,d)};Shape.prototype.init=function(a,b,c,d){this.x=b||0;this.y=c||0;this.color=d;this.canvasContext=a};Circle=function(a){this.constructor(a.context,a.x,a.y,a.color);this.radius=a.radius};Circle.prototype=new Shape;Circle.prototype.draw=function(){this.canvasContext.beginPath();this.canvasContext.fillStyle=this.color;this.canvasContext.arc(this.x,this.y,this.radius,0,Math.PI*2,true);this.canvasContext.fill()};Rectangle=function(a){this.constructor(a.context,a.x,a.y,a.color);this.width=a.width;this.height=a.height};Rectangle.prototype=new Shape;Rectangle.prototype.draw=function(){this.canvasContext.fillStyle=this.color;this.canvasContext.fillRect(this.x,this.y,this.width,this.height)};ImageShape=function(a){this.constructor(a.context,a.x,a.y);this.width=a.width;this.height=a.height;this.src=a.src;this.img=new Image;this.img.src=this.src;this.angle=a.angle};ImageShape.prototype=new Shape;ImageShape.prototype.draw=function(){this.canvasContext.save();this.canvasContext.rotate(this.angle*Math.PI/180);this.canvasContext.drawImage(this.img,this.x,this.y,this.width,this.height);this.canvasContext.restore()};Line=function(a){this.constructor(a.context,a.startX,a.startY,a.color);this.endX=a.endX;this.endY=a.endY;this.lineWidth=a.lineWidth};Line.prototype=new Shape;Line.prototype.draw=function(){this.canvasContext.strokeStyle=this.color;this.canvasContext.lineWidth=this.lineWidth;this.canvasContext.beginPath();this.canvasContext.moveTo(this.x,this.y);this.canvasContext.lineTo(this.endX,this.endY);this.canvasContext.stroke();this.canvasContext.closePath()}